import { useState } from 'react';
import parse from 'html-react-parser';
function App() {
  const [search,setSearch] = useState("");
  const [results,setResults] = useState([]);
  const [searchInfo, setSearchInfo] = useState({});
  const handleSearch = async e => {
    e.preventDefault();
    if(search === '') return;
    const endpoint = `https://en.wikipedia.org/w/api.php?action=query&list=search&prop=info&inprop=url&utf8=&format=json&origin=*&srlimit=20&srsearch=${search}`;
    const response = await fetch(endpoint);
    if(!response.ok){
      throw Error(response.statusText);
    }
    const json = await response.json();
    setResults(json.query.search);
    setSearchInfo(json.query.searchinfo);
  }
  const evaluator = async e => {
    const url = `https://en.wikipedia.org/w/api.php?action=parse&format=json&origin=*&formatversion=2&page=${e}`;
    const response = await fetch(url);
    if(!response.ok){
      throw Error(response.statusText);
    }
    const json = await response.json();
    const indepth = e => {
      if(e.type === 'text'){
        console.log()
      }
    }
    parse(json.parse.text,{
      replace: domNode => {
        if(domNode.attribs){
            if(domNode.attribs.class && domNode.attribs.class === 'summary'){
              const children = domNode.children;
              // console.log(children);
              indepth(children);
              // let temp = '';
              // children.map((e)=>{
                // if(e.data){ 
                    // temp += e.data;
                // }
              // });
              // temp = temp.split("Transcription:")[0];
              // console.log(temp);
            }
        }
      }
    })
  }
  return (
    <div className="App">
      <header>
        <h1>Wiki Seeker</h1>
          <form className="search-box" onSubmit={handleSearch}>
                <input 
                  type="search"
                  placeholder="What are you Searching for?"
                  value={search}
                  onChange={e=>setSearch(e.target.value)}/>
          </form>
       {(searchInfo.totalhits)?<p>Search Result: {searchInfo.totalhits}</p>:
       ''}
      </header>
      <div className="results">
        {results.map((result,i)=>{
          const url = `https://en.wikipedia.org/?curid=${result.pageid}`
          return(
            <div className="result" key={i}>
              <h3>{result.title}</h3>
              <p dangerouslySetInnerHTML={{__html: result.snippet}}></p>
              <a href={url} target='_blank' rel='noreferrer'>Read More</a>
              <button onClick={e=>evaluator(result.title)}> Select </button>
          </div>
          )
        })}
            </div>    
    </div>
  );
}

export default App;
